renderpass.code:
vec3 Flag(vec2 U)
{
	float r = 15. / iResolution.y;
	float q = mod(ceil((U *= r).x - .3), 2.);
	vec4 O = vec4(7, 1, 2, 4) * .1;
	O = (U.x < 11.7 && U.y > 7. ? O.zzwy : sin(2.69 * U.y) < 0. ? O/O : O)
      + smoothstep(r,0.,length(U = vec2(fract(clamp(U.x + .7, 1., 12.)),
        mod(clamp(U.y, 8. - q, 14. + q ) - q * .8, 1.62)) - .5)
      * cos(abs(mod(atan(U.x, U.y) + .6, 1.26) - .6) - 1.26) - .1);	

	return O.rgb;
}

float vignette(vec2 U)
{
    return 16. * U.x * (1. - U.x) * U.y * (1. - U.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7.-6.*iTime+uv.y*6.;
    uv.y += sin(t)*.0125;

    vec3 col = Flag(uv*iResolution.xy);

    col += sin(t)*0.25; // highlights
    col *= .7+cos(t)*.3; // shadows

    col *= 1.0 - .6 * exp2(-1.75 * vignette(uv)); // vignette

    fragColor = vec4(col,1.0);
}
