mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/max(iResolution.x, iResolution.y);

    uv=uv*rotate2d(iTime*3.);
    uv=uv*scale(vec2(sin(iTime)*3.));
    float d=length(uv)*(sin(uv.x*6.+iTime*2.0)+sin(uv.y*6.+iTime*2.)+2.0);

    vec3 color = texture(iChannel0, vec2(d, 0.0)).xyz*1.3;

	fragColor = vec4(color,1.0);
}
