void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = cos(fragCoord/iResolution.y * 5.0);
    vec3 color = vec3(0.4,0.1,1);
    vec2 center = cos(iMouse.xy / iResolution.yy * 5.0);

    color *= distance(center,uv);
    color += max(0., 0.5 - distance(center, uv)) * 2.0;

    fragColor = vec4(color, 1);

}
