// defines/constants
#define SDF_DIST .01
#define RAYMARCH_STEPS 50
#define MAX_DIST 50.0

struct Ray {
    vec3 dir;
    vec3 origin;
};

float GetMinSceneDistanceFromPoint(vec3 point) {
    //define sphere here for now vec4(position.xyz, radius)
    vec4 sphere = vec4(0.0, 1.0, 6.0, 1.0);

    // get distance from point to sphere
    return length(point - sphere.xyz) - sphere.w;
}

float calcShading(vec3 p) 
{
    // light source
    vec3 light_position = vec3(-5.0, 5.0, 2.0);

    // light direction
    vec3 light_dir = normalize(light_position - p);

    // calculate hitpoint normal (gradient of sdf at p)
    float dist = GetMinSceneDistanceFromPoint(p);
    vec2 epsilon = vec2(0.01, 0);
    vec3 normal = normalize(dist - vec3(GetMinSceneDistanceFromPoint(p - epsilon.xyy), 
                                        GetMinSceneDistanceFromPoint(p - epsilon.yxy), 
                                        GetMinSceneDistanceFromPoint(p - epsilon.yyx)));

    // calculate diffuse contribution
    return clamp(dot(normal, light_dir), 0.0, 1.0);
}

float Raymarch(Ray r)
{
    float dist_0 = 0.0; //distance from origin

    // main raymarch loop
    for(int i=0; i < RAYMARCH_STEPS; i++) {

        // march ray from origin in direction
        vec3 t = r.origin + r.dir * dist_0;

        // get distance
        float d = GetMinSceneDistanceFromPoint(t);

        //advance/march along ray
        dist_0 += d;

        //compute sdf
        if(dist_0 > MAX_DIST || d < SDF_DIST) {
            // found a hit
            return dist_0;
        }
    } 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Create ray
    Ray ray;
    ray.dir = normalize(vec3(uv.x, uv.y, 1.0));
    ray.origin = vec3(0.0, 1.0, 0.0);

    // raymarching
    float m = Raymarch(ray);

    if(m < MAX_DIST) {
        // calculate hit point
        vec3 hitpoint = ray.origin + ray.dir * m;

        // shade point
        float diffuse = calcShading(hitpoint);
        fragColor = vec4(vec3(diffuse), 1.0);
    }
    else
    {
        // background
        vec3 col = vec3(uv.xy, 1.0);
        fragColor = vec4(col,1.0);
    }

}
