//This shader is based on Fractal 51_gaz by gaz

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 fragColor,  in vec2 fragCoord)
{
    vec4 O=vec4(0);
	vec2 C = fragCoord;
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<40.;
        O.xyz+=mix(vec3(1),H(log(s)*.45),.4)*.03*exp(-.2*i*i*e) 
    )
    {
        p=g*d +( .005 * texture(iChannel0,O.xy).xy,5.);// vec3(0,0,.5);
        //p+= .001 * texture(iChannel0, vec2(length(C),.3) ).x;
        p=R(p.zxy,normalize(vec3(0.2,0.4,0.9)),iTime*.03);
        s=2.;
        for(int i;i++<6;)
            p=abs(p-vec3(1,2.8,1.5+sin(0.1 * clamp(texture(iChannel0, vec2(length(C),.3) ).x,0.3,.5)-g*.1)*.04))-vec3(1,3.+sin(iTime*.7)*.3,2.1),
            p*=(fract(iTime*.5)>1.)?-1.:1.,
            s*=e=7./clamp(dot(p,p),1.2,7.),
            p*=e;
            p+= clamp(texture(iChannel0, vec2(length(O),.8) ).y,0.1,1.) * .5, 5.;
        g+=e=min(abs(p.z),length(p.xy)-.05)/s+1e-3;
    }

     vec4 cTexture = texture(iChannel1,p.xy);
//     fragColor -= .5 * O - cTexture;
     fragColor =  (.70 + O) - (1. - 0.5 * cTexture);
//     fragColor = mix( 0.7 * O , cTexture, .25);
//     fragColor = max( 0.7 * O , 0.3 * cTexture);


}
