#define PI 3.1415926538

float nsin(in float x)
{
    return sin(x) * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float thickness = 0.005;
    float alias = 3.0 / iResolution.x;
    float speed = 0.05;
    float t = iTime * speed;
    vec3 color;
    for (float i = 0.0; i < 50.0; i += 1.0) {
        float outer = sin((i / 50.0) * PI * 2.0 + t) * 0.25 + 0.25;
        float a = atan(st.y, st.x);
        float d = length(st);
        float f = d
            + sin(iTime * speed * 4.1 + st.y * 10.0) * 0.01
            + sin(iTime * speed * 4.6 + st.x * 9.0) * 0.015
            + sin((a + t) * 16.0) * 0.05 * d
            + sin((0.31 + a + t * 0.77) * 6.0) * 0.03 * d
            + sin((7.71 + a + t * 0.98) * 12.0) * 0.04 * d;
        f *= 1.05;
        float v = smoothstep(0.0, -alias, abs(f - outer + thickness / 2.0) - thickness);
        color += vec3(
            nsin(i * nsin(0.2 + t * 1.23)),
            nsin(i * nsin(2.0 + t * 1.67)),
            nsin(i * nsin(1.0 + t * 1.76))
        ) * v;
    }
    fragColor = vec4(color,1.0);
}
