const float exponent = 2.; // Set to square. Can be changed to any other exponent (like 2 for squaring).
const float zoom = 1.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;

    vec2 offset; // The position of the origin for the polar coordinates to be calculated from

    if (iMouse.xy == vec2(0)) 
        offset = vec2(.5); // If iMouse is 0, set origin to be in the middle of the screen
    else
        offset = iMouse.xy / iResolution.xy; // Set origin to be at mouse position

    uv -= offset; // shift origin to image center
    uv *= zoom; // zoom in/out

    vec2 polar = vec2(length(uv), atan(uv.y, uv.x)); // convert to polar

    polar.x = pow(polar.x, exponent); // Adjust length of polar vector

    uv = polar.x * vec2(cos(polar.y), sin(polar.y)); // convert back from polar

    uv += offset; // shift origin back to lower corner

    // Output to screen
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) // Check if coordinate is outside image
        fragColor = vec4(0, 0, 0, 1);
    else
        fragColor = texture(iChannel0, uv);
}
