void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy - iResolution.xy*.5)/iResolution.y;


    float u = smoothstep(.15-cos(iTime)/10., .21-cos(iTime)/10., length(vec2(uv.x + sin(iTime)/1.5, uv.y)));

    float u2 = smoothstep(.15-cos(iTime)/10., .11-cos(iTime)/10., length(vec2(uv.x + sin(iTime)/1.5, uv.y)));
    vec3 order_flip = vec3(0);

    if (cos(iTime)/10. >= .0) {
        u2 *= u += 1.;
    }
    else {
        u *= u2 += 1.;
    }
    order_flip += u * u2;
    fragColor = vec4(order_flip, .2);
}
