void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord/iResolution.xy) - 0.5;

    uv.x *= iResolution.x/iResolution.y;  // aspect ratio

    // Time varying pixel color
    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float radius = 0.2;

    vec2 left = vec2(uv.x + 0.39, uv.y);
    vec2 right = vec2(uv.x - 0.39, uv.y);
    vec2 leftb = vec2(uv.x + 0.49, uv.y);
    vec2 rightb = vec2(uv.x - 0.49, uv.y);

    color *= smoothstep(radius, radius - 0.01, length(uv)) + 
             smoothstep(radius, radius - 0.01, length(left)) + 
             smoothstep(radius, radius - 0.01, length(right)) + 
             -smoothstep(radius, radius - 0.01, length(leftb)) +
             -smoothstep(radius, radius - 0.01, length(rightb));

    // Output to screen
    fragColor = vec4(color,1.0);
}
