void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 samp = textureLod(iChannel0, 0.25f * fragCoord/1024.0f, 0.0f).rg;

    uvec2 pixels = uvec2(samp * 255.0f);

    uint pixel0 = uint(abs(float(pixels.x) - float(pixels.y)));
    uint pixel1 = uint(pixels.x);
    uint pixel2 = uint(pixels.y);
    uint pixel3 = uint(pixels.x ^ pixels.y);

    float color0, color1, color2, color3;

    color0 = (float(pixel0) / 255.0f);
    color1 = (float(pixel1) / 255.0f);
    color2 = (float(pixel2) / 255.0f);
    color3 = (float(pixel3) / 255.0f);

    vec3 color;

    float t = abs(sin(iTime * 0.5f));

    color.x = mix( color0, color1, t);
    color.x = mix( color.x, mix( color1, color2, t ), 0.5f);
    color.x = mix( color.x, mix( color2, color3, t ), 0.5f);
    color.rgb = color.xxx;              
    fragColor = vec4(color,1.0);
}
