#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define r(O,n) pow(sqrt(O.x*O.x+O.y*O.y),n)
void mainImage(out vec4 v4,in vec2 v2){
  vec2 O=v2,iR=iResolution.xy;    
  O+=iR/8.*vec2(1.57,-1.0)-(iR/2.);
  O*=rot(iTime)/length(iR)*4.5;
  v4=vec4(clamp(r(O,8.)*16.,0.,1.));
  vec3 D3=vec3((1./6.5+5.5/6.5*r(O,1.)),r(O,2.),r(O,3.));
  v4*=2.5-vec4((5./6.+sin(iTime)/6.)*D3,1.5);
v4=clamp(v4,0.,1.);}
