void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 15.0;

    vec2 pos = vec2(10.);
    pos.x += sin(iTime) * 5.;
    pos.y += cos(iTime) * 2.;
    float radius = 5.;
    if (distance(uv, pos) < radius)
    {
        uv -= pos;
        uv /= radius;

        vec2 pc = vec2(length(uv), atan(uv.y, uv.x));
        pc.x = asin(pc.x)/(3.1415927/2.);
        uv = vec2(cos(pc.y), sin(pc.y)) * pc.x;

        uv *= radius;
        uv += pos;
    }

    vec2 a = abs(fract(uv)-.5);
    vec2 d = abs(a) / min(fwidth(uv),0.1);

    fragColor = vec4(min(d.x, d.y));
}
