#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 6.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 6.0;
    float unit = 12.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Axes
    drawSDF(abs(uv.x), vec3(0.0, 0.0, 1.0));
    drawSDF(abs(uv.y), vec3(0.0, 0.0, 1.0));

    // Plot of x^2 - 2
    float y = uv.x * uv.x - 2.0;
    float slope = 2.0 * uv.x;
    drawSDF(abs(uv.y - y) / sqrt(slope * slope + 1.0), vec3(1.0));

    // Roots
    drawSDF(length(vec2(abs(uv.x) - sqrt(2.0), uv.y)) - 0.08, vec3(0.0, 1.0, 0.0));

    // Plot "solution"
    float t = mod(iTime, 3.0);
    float x0 = 8.0 * fract(475.241 * cos(376.584 * floor(iTime / 3.0) + 674.485)) - 4.0;
    float x = sign(x0) * sqrt((x0 * x0 - 2.0) * exp(-t) + 2.0);
    drawSDF(length(uv - vec2(x, 0.0)) - 0.08, vec3(1.0, 0.0, 0.0));

    fragColor = vec4(color, 1.0);
}
