void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int num = int(fragCoord.y * iResolution.y + fragCoord.x);

    int tries = 0;

    while (num > 1)
    {
        if (num % 2 == 1)
        {
            num = num * 3 + 1;
        }
        else
        {
            num /= 2;
        }
        tries++;
    }

    vec3 col = vec3(float(tries)/200.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
