// vec4(Red, Green, Blue, GradientPosition = 0 to 1)
const vec4 Color1 = vec4(1.0, 0.0, 0.0, 0.01);
const vec4 Color2 = vec4(0.0, 1.0, 0.0, 0.4);
const vec4 Color3 = vec4(0.0, 0.0, 1.0, 0.7);
const vec4 Color4 = vec4(1.0, 0.0, 1.0, 0.9);


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 ScreenUV = fragCoord/iResolution.xy;
    float TexSample0 = texture(iChannel0, ScreenUV).r;

#if 0
    TexSample0 = TexSample0 + fract(iTime*0.333);
    if (TexSample0 > 1.0) {TexSample0 = TexSample0 - 1.0;}
#endif

    // Color mix value:  (TextureValue - ColorPos.Start)  /  ( Distance between ColorPos.Start and ColorPos.End )
    vec3 TheColor;
    if      (TexSample0 < Color1.a) { TheColor = Color1.rgb; }
    else if (TexSample0 < Color2.a) { TheColor = mix( Color1.rgb, Color2.rgb, (TexSample0 - Color1.a) / (Color2.a - Color1.a) ); }
    else if (TexSample0 < Color3.a) { TheColor = mix( Color2.rgb, Color3.rgb, (TexSample0 - Color2.a) / (Color3.a - Color2.a) ); }
    else if (TexSample0 < Color4.a) { TheColor = mix( Color3.rgb, Color4.rgb, (TexSample0 - Color3.a) / (Color4.a - Color3.a) ); }
    else                            { TheColor = Color4.rgb; }

    fragColor = vec4(TheColor, 1.0);
}
