void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

// MyFirstShader

// GeoffSmithee

// combined some code fron the initial "new" and from a youtube video
// https://www.youtube.com/watch?v=u5HAYVHsasc


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= 0.5; // -.5 <> .5

    uv.x *= iResolution.x/iResolution.y; //adjust x by aspect ratio

    float d = length(uv);    // vector length sqrt((uv.x*uv.x) + (uv.y*uv.y))
    float r = 0.3;           // radius of circle

    float c = d;



    //float c = smoothstep( r, r-0.02, d);

    vec3 col;

    if ( d > .3) col = vec3 (c); else col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
