void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

    float alpha = (sin(iTime) + 1.0) / 2.0;

    float size = alpha * 100.;

    bool inside = pow(fragCoord.x - iResolution.x / 2., 2.) 

                  + pow(fragCoord.y - iResolution.y / 2., 2.) < pow(size, 2.) ;



    fragColor = vec4(0.0,0.0,0.0,1.0);



    if (inside) fragColor = vec4(alpha, alpha, alpha,1.0);

}
