#define SQR(x)  ( (x)*(x) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U = u/R;
    float x = U.x;

    vec4 a0 = vec4(.1), 
         a1 = mod(iTime,2.)<1. ? vec4(1) : vec4(4,2,1,0);
                              // with grey   with colors
    int i = int(7.*U.y);
//  if the right unit is F(a), then we do F⁻¹( mix( F(a0),F(a1),x ) )    
    O = vec4(   i==6 ? mix( a0, a1, x )                    // transmit
              : i==5 ? 1.-mix( 1.-a0, 1.-a1, x )           // opacity
              : i==4 ? 1./mix( 1./a0, 1./a1, x )           // 
              : i==3 ? sqrt( mix( a0*a0, a1*a1, x ) )      // energy
              : i==2 ? SQR( mix( sqrt(a0), sqrt(a1), x ) ) //
              : i==1 ? exp( mix( log(a0), log(a1), x ) )   // magnitude
              :        log( mix( exp(a0), exp(a1), x))     //
            );

    O += 1./256.*texelFetch(iChannel0,ivec2(u)%1024,0);    // suppress Mach bands
    if ( int(7.*u.y)%int(R.y) < 7 ) O =  vec4(0,1,0,0);    // separators
}
