#define pi 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    float ModTime = (uv.x - uv.y) - mod(iTime * 2., 2.) + 1.;
    if (ModTime > -.05 && ModTime < .05) uv.x -= .2;

    float piTime = iTime * pi;
    float CosdTime = cos(piTime * .2) * .5 + .5;
    float CosdTime2 = cos(piTime * .2 - pi) * 80. + 120.;
    float blur = .3 + CosdTime * .5;
    float blur2 = .6 - blur;

    uv.x -= uv.y * blur;

    float num1 = smoothstep(blur, blur2 , cos(uv.x * CosdTime2 - CosdTime * 1.5) * .5 + .5);
    float num2 = smoothstep(blur, blur2 , cos(uv.x * CosdTime2) * .5 + .5);
    float num3 = smoothstep(blur, blur2 , cos(uv.x * CosdTime2 + CosdTime * 1.5) * .5 + .5);

    // Output to screen
    fragColor = vec4(vec3(num1, num2, num3), 1.0);
}
