#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=1.;
        ++i<80.;
        O.xyz+=.04*abs(cos(d+log(s)*.3))*exp(-i*i*e)
    )
    {
        p=g*d;
        p.z-=mix(.5,10.,step(.5,fract(iTime*.1)));
        p=R(p,normalize(vec3(1,2,3)),iTime*.2);
        q=p;
        s=1.5;
        for(int j=0;j++<12;)
            p=sign(p)*(1.5-abs(p-1.5)),
            p=p*(e=8./clamp(dot(p,p),.5,5.))+q,s*=e;
        g+=e=length(p)/s;
    }
}
