void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - (iResolution.xy / vec2(2.0));
    float x = float(coord.x);
    float y = float(coord.y);
    float iiTime= 160.0;
    fragColor = vec4(0.0,0,0,1);
    if(x*x+y*y<iiTime*iiTime/5.0)
        fragColor = vec4(
            x*1.28/iiTime,
            y*1.28/iiTime,
            abs(x+y)/iiTime,
            1
        );
    if (
        x*x+y*y>iiTime*iiTime ||
        x*x+y*y<iiTime*iiTime/5.0 &&
        abs(int(x))<=int(iiTime)  && 
        abs(int(y)) <= int(iiTime)&& 
        abs(int(x))<=abs(int(sqrt(5.0)*y-iiTime)) &&
        abs(int(x))<=abs(int(sqrt(5.0)*y+iiTime)) &&
        abs(int(y))<=abs(int(sqrt(5.0)*x-iiTime)) &&
        abs(int(y))<=abs(int(sqrt(5.0)*x+iiTime)))
            vec4(1,1,1,1);
    else if (
        abs(int(x))<=abs(int(sqrt(5.0)*(y+iiTime))) && 
        abs(int(x))<=abs(int(sqrt(5.0)*(y-iiTime))) &&
        abs(int(y))<=abs(int(sqrt(5.0)*(x+iiTime))) &&
        abs(int(y))<=abs(int(sqrt(5.0)*(x-iiTime))))
            fragColor = vec4(
                abs(x)/abs(y),
                abs(y)/abs(x),
                abs(x-y)/abs(x+y),
                1);
}
