#define speed 10.0
float cwawe(float strokeWidth,float freq,vec2 uv){
    float val = cos(iTime*speed - uv.x*freq)/2.0+2.0;
    if(uv.y >= val && uv.y <= val+strokeWidth)return val*0.5;

    return 0.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*4.0;

    // Time varying pixel color
    float w = cwawe(0.2,10.0,uv);
    vec3 col = vec3(w) + vec3(cos(uv.x),cos(uv.y),cos(iTime));
    if(w == 0.0)col = vec3(0.0);
    // Output to screen
    fragColor = vec4(col,1.0);
}
