void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float dotResolution = 30.;
  vec2 uv = fragCoord / iResolution.xy;
  vec4 color = texture(iChannel0, uv);

  // RGB-to-CMYK conversion
  vec4 cmyk;
  cmyk.xyz = 1.0 - color.rgb;
  cmyk.w = min(cmyk.x, min(cmyk.y, cmyk.z));
  cmyk.xyz -= cmyk.w;

  vec2 mUV = dotResolution*uv;
  vec2 mUV2 = 2.0*fract(mUV)-1.0;
  float mR = sqrt(cmyk.y);
  float m = smoothstep(mR + 0.1, mR - 0.1, length(mUV2));

  vec3 rgb = 1. - vec3(0., m, 0.);
  fragColor = vec4(rgb, 1.0);
}
