#define S(r) smoothstep(0., -20./iResolution.y,r) //Thanks FabriceNeyet
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (gl_FragCoord.xy-.5*iResolution.xy) /iResolution.y;

	float col = 0.;

	vec2 fractionalUV = fract( uv*10.)-.5;
	float calcOutput = 0.;             
    float t = iTime*2.;                 
    for (float y=-1.; y<=1.; y++){      
        for (float x=-1.; x<=1.; x++){
            vec2 offset = vec2(x, y);  
			float d = length(fractionalUV+offset);  
			float r = mix(.3, .5, sin((t)+length(sin(uv+(fractionalUV)))*(sin(t*.3)*20.+20.*(dot(uv.x,uv.y))))*.5+.5*d);
         	calcOutput += S(d-r);
            col = calcOutput;

        }	
	}


	vec4 color = vec4(vec3(col), 1.0);
    vec2 texuv = fragCoord.xy / iChannelResolution[0].xy;
    texuv += vec2(-.5);
    texuv *= 1.1;
    texuv += vec2(+.5);
    vec4 feedback = texture(iChannel0,texuv);


    fragColor = color+(-feedback*(sin(iTime)*.5+.5));


}
