vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
float runes(vec2 uv){
    vec2 tuv = fract(uv);
    vec2 id = floor(uv);

    if( rand(id)<0.3 ) tuv.x=1.-tuv.x;
    if( rand(id)>0.6 ) if(rand(id)>0.8){tuv.x*=1.-tuv.x;} else {tuv.y*=1.-tuv.y;}
    float runes = 1. - step(0.9, abs(abs(tuv.x-tuv.y)-.5)*10. );
    return runes;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    int iterations = 5;
    float s=0.5*sin(iTime*0.01);
	for(int i=0;i<iterations;i++){
		uv=abs(uv)-s;
		uv=rot(uv,iTime*0.1);
		s=s/2.;
	}

    uv*=20.;
    uv-=iTime*2.;
    float runes = runes(uv);
    vec4 texture = texture(iChannel0,uv);

    vec3 col = vec3(1.,0.,0.)*runes;
    //col = texture.rgb;

    fragColor = vec4(col,1.0);
}
