float BlendOverlay(float base, float blend) {
    return float(
        base < 0.5 ? (2.0 * base * blend) : (1.0 - 2.0 * (1.0 - base) * (1.0 - blend))
    );
}
vec2 BlendOverlay(vec2 base, vec2 blend) {
    return vec2(
        base.x < 0.5 ? (2.0 * base.x * blend.x) : (1.0 - 2.0 * (1.0 - base.x) * (1.0 - blend.x)),
        base.y < 0.5 ? (2.0 * base.y * blend.y) : (1.0 - 2.0 * (1.0 - base.y) * (1.0 - blend.y))
    );
}
vec3 BlendOverlay(vec3 base, vec3 blend) {
    return vec3(
        base.r < 0.5 ? (2.0 * base.r * blend.r) : (1.0 - 2.0 * (1.0 - base.r) * (1.0 - blend.r)),
        base.g < 0.5 ? (2.0 * base.g * blend.g) : (1.0 - 2.0 * (1.0 - base.g) * (1.0 - blend.g)),
        base.b < 0.5 ? (2.0 * base.b * blend.b) : (1.0 - 2.0 * (1.0 - base.b) * (1.0 - blend.b))
    );
}
vec4 BlendOverlay(vec4 base, vec4 blend) {
    return vec4(
        base.r < 0.5 ? (2.0 * base.r * blend.r) : (1.0 - 2.0 * (1.0 - base.r) * (1.0 - blend.r)),
        base.g < 0.5 ? (2.0 * base.g * blend.g) : (1.0 - 2.0 * (1.0 - base.g) * (1.0 - blend.g)),
        base.b < 0.5 ? (2.0 * base.b * blend.b) : (1.0 - 2.0 * (1.0 - base.b) * (1.0 - blend.b)),
        base.a < 0.5 ? (2.0 * base.a * blend.a) : (1.0 - 2.0 * (1.0 - base.a) * (1.0 - blend.a))
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 ScreenUV = fragCoord/iResolution.xy;

    vec3 TexSample0 = texture(iChannel0,ScreenUV).rgb;
    vec3 TexSample1 = texture(iChannel1,ScreenUV).rgb;

    vec3 TheColor = BlendOverlay(TexSample0 ,TexSample1);

    fragColor = vec4(TheColor,1.0);
}
