#define WATER_STRENGTH 0.02
#define WATER_FREQ 20.0

#define WAVE_STRENGTH 0.04
#define WAVE_FREQ 32.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float hp = (sin(iTime * 0.1) + 1.0) / 2.0; // 0.0 - 1.0

    vec2 waterOffset = vec2(sin(iTime + uv.y * WATER_FREQ) * WATER_STRENGTH, 0.0);

    float waveOffset = sin(iTime + uv.x * WAVE_FREQ) + sin(iTime * 2.0 + uv.x * (WAVE_FREQ / 2.0));
    waveOffset *= (1.0 / 2.0) * WAVE_STRENGTH;

    vec3 col = texture(iChannel0, uv + step(hp + waveOffset, uv.y)*waterOffset).rgb * step(distance(uv, vec2(0.5)), 0.5);

    if (uv.y > hp + waveOffset) {
        col = vec3(col.g);
        col /= 2.0;
    }

    fragColor = vec4(col,1.0);
}
