// sd functs from https://iquilezles.org/articles/distfunctions2d

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle(in vec2 p, in float r)
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= aspectRatio;

    float dB = abs(sdBox(uv, vec2(0.5 * aspectRatio, 0.5))) - 0.025;
    float dC = sdCircle(uv - vec2(sin(iTime) * 1.41, cos(iTime)) / aspectRatio, 0.35);

    float morph = mix(dB, dC, 0.5);

    vec3 col = vec3(dB < 0.0, 0.0, 0.0);

    col += vec3(morph < 0.0);

    fragColor = vec4(col, 1.0);
}
