// by mathfigure 2022.02.18
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float max = 180.0;

    vec2 pos = vec2(0.0, 0.0);
    float scale = 1.125;

    vec2 z = ((2.0*fragCoord - iResolution.xy) / iResolution.y + pos) * scale;
    vec2 c = iFrame < 2 ? vec2(-0.768,-0.1) : ((2.0*iMouse.xy - iResolution.xy) / iResolution.y + pos) * scale;

    float i = 0.0;
    while(length(z) < 2.0 && ++i<max)
        z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;

    fragColor = vec4(sin(i/10.0 - 0.5), sin(i/11.0), sin(i/12.0 + 1.0), 1);
}
