float PI = 3.14,
     PHI = 1.61,
     Rd0 = .5,
      R0 = .45,
      dR = .9,
       n = 10.;

float drawCircles(vec2 U, float Rd, float R, float R0) {

    float r = length(U), c = 0.;

    for(float r=0.,i=0.,a=0.; i++ < n; a += 2.*PI/n)
        r = length(U - Rd * vec2( -sin(a), cos(a)) ),
        c += exp(-abs(r - R0)/.008);

    return c;
}

void mainImage( out vec4 O, vec2 u ) {
    vec2 R = iResolution.xy, 
        U = ( 2. * u - R ) / R.y;   
    O = drawCircles(U, Rd0, R0, R0*dR) * vec4(1,1,0,0);
}
