int colonne = 0;
int ligne = 0;

/// Blue (240,248,255)
vec3 Creationdamier(vec2 uv)
{
    float interpolation = (iTime -  float(int(iTime))); 
    vec3 color ;
    for (colonne = 0; colonne < 20; colonne++ ){

       for (ligne = 0; ligne < 20; ligne++ ){

          if(float(colonne)*1./20. < uv.x && uv.x < float(colonne)+1.*1./20. && float(ligne)* 1./20. < uv.y && uv.y < float(ligne)+1.*1./20.)
          {
             if(colonne == ligne)
             {
                if(interpolation < 0.5){
                color = vec3(0.3,0.5,1.);

                }
                else{
                color = vec3(1.,1.,0.3);
                }
             }
             else if((ligne+colonne) %2 == 1)
             {
                 color = vec3(0.,0.,0.);
             }
             else{

             color = vec3(1.,1.,1.);
             }
          }
        }
    }
    return color ;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(Creationdamier(uv));

	fragColor = vec4(color, 1.0);
}
