#define k 360.0

vec2 rotate(vec2 xy, float a){
  return vec2(xy.x*cos(a)-xy.y*sin(a),xy.x*sin(a)+xy.y*cos(a));
}

float gear2(vec2 xy, float angle){
    xy=rotate(xy, angle);
    if(length(xy)<k/10.5&&
    length(xy)>k/36.0)
        return sin(length(xy));
    return 0.0; 
}

bool gear(vec2 xy, float angle){
    xy=rotate(xy, angle);
    float rad=atan(xy.y,xy.x);
    if((length(xy)<k/10.5&&length(xy)>k/11.5||
    (abs(xy.x)<1.5||abs(xy.y)<1.5||abs(xy.x+xy.y)<2.0||abs(xy.x-xy.y)<2.0)&&length(xy)<k/11.0||length(xy)<k/29.0 )&&
    length(xy)>k/36.0)
        return true;
    return false; 
}

void mainImage(out vec4 rgba, in vec2 xy)
{
    float t=iTime;
    rgba=vec4(0.5,0.75,1.0,1.0);
    xy-=iResolution.xy/2.0;xy.x-=k/16.0;
    if(xy.y+sin(xy.x/8.0)*2.0<0.0)rgba=vec4(0.4,0.2,0.0,1.0);
    if(abs(xy.y+sin(xy.x/8.0)*2.0)<2.0)rgba/=3.0;
    if(mod(xy.x,4.0)<2.0&&mod(xy.y,4.0)<2.0)rgba/=2.0;
    vec4 color2=vec4(1.0,0.25,0.5,1);
    float g1=abs(gear2(vec2(mod(xy.x,k/4.0)-k/8.0,xy.y),t));
    float g2=abs(gear2(vec2(mod(xy.x+k/8.0,k/4.0)-k/8.0,xy.y),t));
    if(g1>0.0&&xy.y>0.0&&abs(xy.x)<k/2.0)rgba=color2*(g1+1.0)*1.0;
    if(g2>0.0&&xy.y<0.0&&abs(xy.x+k/8.0)<k/2.0)rgba=color2*(g2+1.0)*1.0;
    if(gear(vec2(mod(xy.x,k/4.0)-k/8.0,xy.y),-t)&&xy.y>0.0&&abs(xy.x)<k/2.0)rgba*=2.0;
    if(gear(vec2(mod(xy.x+k/8.0,k/4.0)-k/8.0,xy.y),t)&&xy.y<0.0&&abs(xy.x+k/8.0)<k/2.0)rgba*=2.0;
    if(length(xy+vec2(k/1.78,0))<k/30.0&&xy.y>0.0) rgba=color2*(g1+1.0)/1.0;
    if(length(xy-vec2(k/2.29,0))<k/30.0&&xy.y<0.0) rgba=color2*(g2+1.0)/1.0;
    if(length(xy+vec2(k/1.78,0))<k/30.0&&length(xy+vec2(k/1.78,0))>k/40.0&&xy.y>0.0) rgba*=2.0;
    if(length(xy-vec2(k/2.29,0))<k/30.0&&length(xy-vec2(k/2.29,0))>k/40.0&&xy.y<0.0) rgba*=2.0;

}
