float lagr(vec2 uv, vec2 a, vec2 b, vec2 c, vec2 d)
{
    return (a.y * (uv.x-b.x)*(uv.x-c.x)*(uv.x-d.x)) / ((a.x-b.x)*(a.x-c.x)*(a.x-d.x));
}

float spot(vec2 uv, vec2 p, float b)
{
    return smoothstep(0.2,0.02,length(uv-p) / b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord - 0.5 * iResolution.xy)/iResolution.y) * 2.0;

    vec3 col = vec3(0.0);
    float t = iTime * 0.2;

    // define some moving points
    vec2 p0 = vec2(-0.82,0.033) + vec2(0.2*sin(iTime),0.4 * cos(iTime)) ;
    vec2 p1 = vec2(-0.2,0.1) + vec2(0.3*cos(iTime),0.3 * sin(t));
    vec2 p2 = vec2(0.3,-0.13)+ vec2(0.12*sin(t),0.3 * cos(iTime));
    vec2 p3 = vec2(0.8,0.3)+ vec2(0.22*cos(t),0.34 * sin(t));

    // lagrange interpolation
    float l0 = lagr(uv,p0,p1,p2,p3);
    float l1 = lagr(uv,p1,p0,p2,p3);
    float l2 = lagr(uv,p2,p1,p0,p3);
    float l3 = lagr(uv,p3,p1,p2,p0);

    float lgr = l0+l1+l2+l3;

    // draw the points
    col += spot(uv,p0,0.06);
    col += spot(uv,p1,0.06);
    col += spot(uv,p2,0.06);
    col += spot(uv,p3,0.06);

    // show the curve through all points
    col += vec3(smoothstep(0.01,0.003,(abs(uv.y-lgr)))) * 0.6;
    // show the curves that are added together to get the above
    col.r += smoothstep(0.01,0.003,(abs(uv.y-l0)));
    col.g += smoothstep(0.01,0.003,(abs(uv.y-l1)));
    col.b += smoothstep(0.01,0.003,(abs(uv.y-l2)));
    col.gb +=smoothstep(0.01,0.003,(abs(uv.y-l3)));

    fragColor = vec4(col,1.0);
}
