void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.y) * 1.5;
    uv.y += 0.4;
    vec2 z = vec2(0.0);
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * (z.x * z.x - 3.0 * z.y * z.y), -abs(z.y * (3.0 * z.x * z.x - z.y * z.y))) + uv;
        if(dot(z, z) > 65536.0) break;
    } // Divide iterations by maximum
    fragColor = vec4(vec3(sqrt(l / 100.0)) * vec3(1.1, 0.6, 0.0) * 1.5, 1.0);
}
