bool circle(vec2 p, vec2 c, float r)
{
 float scaleY = iResolution.y / iResolution.x;
    float x = p.x - c.x;
    float y = p.y - c.y;
    return (x) * (x) + (y) * (y) * (scaleY * scaleY) < r*r; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 crd = fragCoord/iResolution.xy;

    float x = crd.x;
    float y = crd.y;



    fragColor = vec4(1.0, 1.0, 1.0, .0);

    // body
    if (circle(vec2(x, y), vec2(0.69, 0.52), 0.055))
        fragColor = vec4(.9, .5, .0, 1.0);

    if (circle(vec2(x, y), vec2(0.6, 0.5), 0.06))
        fragColor = vec4(1., .0, .0, 1.0);

    if (circle(vec2(x, y), vec2(0.51, 0.48), 0.055))
        fragColor = vec4(.0, .0, 1., 0.0);

    // right front leg
    for (float i=0.; 6.0>i ; i++)
    {
        if (circle(vec2(x, y), vec2(0.45 - 0.02*i, 0.39 - 0.08*i), 0.03))
           {
            fragColor = vec4(.9-0.1*i, .5, .0+0.1*i, 1.0);
           }
    }


    // left front leg
    for (float i=0.; 6.0>i ; i++)
    {
        if (circle(vec2(x, y), vec2(0.52 + 0.02*i, 0.39 - 0.08*i), 0.03))
           {
            fragColor = vec4(1.-0.1*i, .5, 5.+0.1*i, 1.0);
           }
    }

    // back leg
    for (float i=0.; 6.0>i ; i++)
    {
        if (circle(vec2(x, y), vec2(0.74 + 0.02*i, 0.42 - 0.08*i), 0.03))
           {
            fragColor = vec4(1.-0.2*i, 0., 5.+0.1*i, 0.);
           }
    }
    // tail
    if (circle(vec2(x, y), vec2(0.74, 0.64), 0.03))
        fragColor = vec4(0., 1.0, .0, 1.0);

    if (circle(vec2(x, y), vec2(0.76, 0.72), 0.03))
        fragColor = vec4(0., 0.5, 1.0, 0.4);

    if (circle(vec2(x, y), vec2(0.78, 0.8), 0.03))
        fragColor = vec4(1., 0.0, .0, 1.0);

    if (circle(vec2(x, y), vec2(0.82, 0.88), 0.05))
        fragColor = vec4(1., 1., .0, 1.0);



    float xd = 0.0;
    float yd = 0.0;
           xd = xd+0.01 * sin(iTime);
           yd = yd+0.01 * sin(iTime);

    // neck
    for (float i=0.; 4.0>i ; i++)
    {
        if (circle(vec2(x, y), vec2(0.47 - 0.02*i -xd*i, 0.58 + 0.05*i-yd), 0.02+0.003*i))
           {
            fragColor = vec4(1.-0.2*i, 0., 5.+0.1, 0.);
           }
    }
    // head
    if (circle(vec2(x, y), vec2(0.4-xd*4.0, 0.95-yd), 0.03))
        fragColor = vec4(1.0, 0.0, .0, 0.0);
    if (circle(vec2(x, y), vec2(0.36-xd*4.0, 0.85-yd), 0.07))
        fragColor = vec4(1.0, 1.0, .0, 1.0);
    if (circle(vec2(x, y), vec2(0.44-xd*4.0, 0.9-yd), 0.03))
        fragColor = vec4(0.0, 1.0, .0, 0.0);
    if (circle(vec2(x, y), vec2(0.37-xd*4.0, 0.85-yd), 0.02))
        fragColor = vec4(1.0, 1.0, 1., 0.0);
    if (circle(vec2(x, y), vec2(0.37-xd*4.0, 0.85-yd), 0.01))
        fragColor = vec4(0.0, 0.0, .0, 0.0);
    if (circle(vec2(x, y), vec2(0.3-xd*4.0, 0.85-yd), 0.02))
        fragColor = vec4(0.0, 0.0, 1.0, 0.0);
    if (circle(vec2(x, y), vec2(0.32-xd*4.0, 0.78-yd), 0.01))
        fragColor = vec4(0.0, 1.0, 1.0, 0.0);


}
