#define fwidth(v)   sqrt( dFdx(v)*dFdx(v) + dFdy(v)*dFdy(v) )                 
#define F           atan(x)*sin(3.*x) - y 
#define D           O -=  x < 1.7 ? c* clamp( 4.*R.y/288. - abs(v)/fwidth(v) ,0.,1.): 0.;

float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.); 
    return length(p - b * h);                      
}


void mainImage(out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, d,
          U = 1.2*( u+u - R ) / R.y;
    U *=  mat2(cos(.7+vec4(0,11,33,0)));
    O = vec4(1);
    float x = U.x,y,c=1., 
    v = length(U)-1.06; D                              // disk
    v -= .1;                           D

    y = U.y -= .5; v = length(U) -.15;
    if (x+.2*y > .05 )                 D               // head
    v += .1;                           D
    v = line(U, vec2(-.12,-.25),vec2(-.12, .25)); D
    v = line(U, vec2( .13,-.25),vec2( .10,-.1 )); D
    v = line(U, vec2(-.12,-.15),vec2( .11,-.15)); D
    v = line(U, vec2(-.12,-.22),vec2( .11,-.22)); D
    v = line(U, vec2(-.03, .05),vec2(-.12, .25)); D
    U -= d = vec2(-.22,.12); if(x+1.5*y>.21) v = length(U) -.25, D // beak
    U += d;
    d -= vec2(.09,.03);
    U -= d; if(x+1.5*y>.08) v = length(U) -.25, D
    U += d;

    U.y += 1.3;                                        // body
    float r = length(U), a = abs(atan(U.x,U.y)); y = r-1.;

    x = 1.3*a,  v = .5*F;              D
    x = 1.4*a,  v = .5*F - .1;         D
 // if ( abs(v)/fwidth(v) > 6.*R.y/288. ) {
    c = clamp( abs(v)/fwidth(v) / ( 2.*R.y/288.)-2.5,0.,1.); {
        x = 1.6*a,  v = .3*F - .1;     D
        x = 1.7*a,  v = .3*F - .2;     D
        x = 1.9*a,  v = .3*F - .3;     D
    } c=1.;
    x = abs(U.x), y = U.y-.3; r = sqrt(x*x+y*y);       // tail
    if ( r < .5) { 
        v = .2/x - y -1.; D
        v = .1/x - y -1.; D
    }
    if ( y < -.33 ) v = r -.47,        D
}
