void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, vec2(uv.x, uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float dx = 1. / iResolution.x;
    float dy = 1. / iResolution.y;

    vec4 neighbourColor = vec4(0.,0.,0.,0.);
    float r = texture(iChannel0, vec2(uv.x, uv.y)).r;
    float cellVal = r > 0.5 ? 1. : 0.;
    float neighbourTotal = 0. - cellVal; // Exclude self
    for (float x = -1.; x < 1.; x++) {
        for (float y = -1.; y < 1.; y++) {
            neighbourColor = texture(iChannel0, vec2(uv.x - (x * dx), uv.y - (y * dy)));
            neighbourTotal += (neighbourColor.r > 0.5 ? 1. : 0.);
        }
    }

    if ((cellVal == 1. && neighbourTotal == 2.) || neighbourTotal == 3.) {
        fragColor = vec4(1., 1., 1., 1.);
    } else {
        fragColor = vec4(0., 0., 0., 0.);
    }
}
