bool circle(vec2 uv, vec2 o , float r)
{
    float scaleY = iResolution.y/iResolution.x;
    return (uv.x - o.x)*(uv.x - o.x) + (uv.y - o.y)*(uv.y - o.y)* (scaleY * scaleY) < r*r; 
}

bool ellipse(vec2 o, vec2 uv, vec2 n, float r)
{
     float scaleY = iResolution.y/iResolution.x;
     return (n.x*(uv.x - o.x)*(uv.x - o.x) + n.y*(uv.y - o.y)*(uv.y - o.y)* (scaleY * scaleY)) <= r;
}

bool triangle (vec2 a, vec2 b, vec2 c, vec2 uv)
{ 
     float n = (a.x - uv.x)*(b.y - a.y) - (b.x - a.x)*(a.y - uv.y);
     float m = (b.x - uv.x)*(c.y - b.y) - (c.x - b.x)*(b.y - uv.y);
     float p = (c.x - uv.x)*(a.y - c.y) - (a.x - c.x)*(c.y - uv.y);
     return(n >= 0. && m >=0.0 && p >= 0.0) || (n <= 0.0 && m <= 0.0 && p <= 0.0);  
} 

