    bool circle(vec2 point, vec2 o, float r){
             float scaleY = iResolution.y / iResolution.x; // коэффициент по ОУ
              return (point.x - o.x) * (point.x - o.x) + (point.y - o.y) * (point.y - o.y) * (scaleY * scaleY) < r*r;//check if the point inside the circle
    }
