    float Circle(vec2 uv, vec2 p, float r, float blur) {
                 float d = length(uv-p);
                  float c = smoothstep(r, r-blur, d);
                   return c;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
          uv -= .5; // -0.5 <> 0.5

    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(0.);

           uv.x *= iResolution.x/iResolution.y;


     float c1 = Circle(uv, vec2(-.4, .3), .14, .01);
     float c2 = Circle(uv, vec2(-.3, .12), .09, .01);
     float c3 = Circle(uv, vec2(-.2, -.05), .13, .01);
     float c4 = Circle(uv, vec2(.02, -.05), .13, .01);
     float c5 = Circle(uv, vec2(.25, -.05), .13, .01);
     float c6 = Circle(uv, vec2(.37, -.2), .1, .01);
     float c7 = Circle(uv, vec2(.48, -.35), .1, .01);
     float c8 = Circle(uv, vec2(-.3, -.23), .1, .01);
     float c9 = Circle(uv, vec2(-.4, -.37), .1, .01);
     float c10 = Circle(uv, vec2(-.45, .35), .05, .01);
     float c11 = Circle(uv, vec2(-.3, .41), .05, .01);
     float c12 = Circle(uv, vec2(.37, .06), .05, .01);

      col = vec3(1., 1., 0.)*c1;
       col += vec3(1., 0., 1.)*c2;
       col += vec3(0., 1., 1.)*c3;
       col += vec3(0., 0., 1.)*c4;
       col += vec3(1., 0., 0.)*c5;
       col += vec3(0., 1., 0.)*c6;
       col += vec3(0.5, 1., 0.5)*c7;
       col += vec3(0.5, 0.5, 0.5)*c8;
       col += vec3(1., 1., 1.)*c9;
       col -= vec3(1., 1., 1.)*c10;
       col += vec3(1., 0., 0.)*c11;
       col += vec3(1., 1., 1.)*c12;

        fragColor = vec4(col, 1.0);
}
