const float sqR = 200.0;
const float R = sqR * sqR;


float wave(float x){
    return (sin(x * 5.0 + iTime) * cos(iTime) * 0.2 + 0.5);
}

float r(vec2 rr){
    return (rr.x * rr.x * wave(iTime* 2.0) + rr.y * rr.y * wave(iTime) * 1.5);
}

float atan2(float y, float x){
    return (x < 0.0) ? (atan(- y / x)) : (atan(y / x));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 mid = vec2(iResolution.x * 0.5 + 0.5, iResolution.y * 0.45 + 0.5);


    vec2 XY = fragCoord.xy / iResolution.xy;//Condensing this into one line

    vec2 xy = fragCoord.xy;

    vec2 rv = xy - mid;

    float ang = atan2(rv.y, rv.x);

    if ( r(rv)  < R * wave(ang)){
        fragColor = vec4(texture(iChannel0, XY).xyz, 1);
    }
    else{
        fragColor = vec4(texture(iChannel1, XY).xyz, 1);
    }

}

vec2 mainSound( float time )
{
    return vec2( sin(time * 1000.0), sin(time * 1000.0) );
}
