#define vec vec2(x,y)

    bool circle(vec2 point, vec2 centr, float r){
             float scaleY = iResolution.y / iResolution.x; // для адаптации круга под размер окна
             float dx = (point.x - centr.x) * (point.x - centr.x);
             float dy = (point.y   - centr.y) * (point.y - centr.y);
              return dx + dy * (scaleY * scaleY) < r*r; 
}

