void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.0);

  vec2 mxy = (iMouse.xy-0.5*iResolution.xy)/max(iResolution.x,iResolution.y);

  {
  	float rad = 0.05;
  	float blur = 0.25;
  	float shape = smoothstep(rad*(blur-0.01),rad,length(uv-mxy));
  	float bshape = step(rad,length(uv-mxy));
    if(bool(bshape)) discard;
    else
      col += 1.0-vec3(shape);
      col *= vec3(sin(iTime),cos(iTime),atan(iTime));
  }

  fragColor = vec4(col,1.0);
}
