void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    //Square
    vec2 uv = fragCoord/iResolution.xy;

    float aspect = iResolution.y / iResolution.x;
    vec3 col = vec3(0);

    float top = 0.6;
    float bottom = 0.25;
    float right = 0.6;
    float left = 0.4;


    if (uv.y < top && uv.y > bottom && uv.x > left && uv.x < right)
    {

        col = vec3(0, 1.0, 1.0);
    }

    // circle
    vec3 col2;

    uv.y *= aspect;

    float radius = 0.06;
    vec2 center = vec2(0.7);
    center.y *= aspect;

    if (distance(center, uv) < radius)
    {
        col2 = vec3(0, 1.0, 1.0);
    }

    // circle




    radius = 0.06;

    center = vec2(0.2);
    center.y *= aspect;

    if (distance(center, uv) < radius)
    {
        col2 = vec3(75.0, 1.0, 1.0);
    }

    radius = 0.06;
    center = vec2(0.3);
    center.x *= aspect;
    float stepResult = step(distance(center, uv), radius);
    vec3 col3 = vec3(stepResult);

    // giant smooth circle
    radius = 0.06;
    center = vec2(0.8);
    center.x *= aspect;
    float smoothResult = smoothstep(0.7, 0.9, distance(center, uv));
    vec3 col4 = vec3(smoothResult);


    // Output to screen
    fragColor = vec4(col,1.0) + vec4(col2, 1.0) + vec4(col3, 1.0) + vec4(col4, 1.0);
}
