void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float PI = 3.1415926f;
    const float thickness = .02f;
    const float speedFactor = .4f;
    vec2 uv = fragCoord/iResolution.xy;

    float yAxis = uv.y - .5f;
    float amplitude = .5f * sin(iTime);
    float passingSine = sin((uv.x + iTime * speedFactor) * 2.f * PI);

    float color = abs(yAxis - amplitude * passingSine) / thickness;

    // Output to screen
    fragColor = vec4(color, color, color, 1.0);
}
