void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    float lines = 12. - floor((iMouse.x / iResolution.x) * 10.);
    float num = lines / 2.;
    float d = length(uv * sin(iTime + num * atan(uv.x, uv.y)));
    d += length(uv) * 2.;
    d = sin(d * 10. - iTime);
    d = smoothstep(0.5, 0.5, d);
    vec3 col = vec3(d);
    fragColor = vec4(col,1.0);
}
