#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
    for(;++i<99.;)
    {
        p=g*d;
        p.z-=t*.5;
        p=R(p,vec3(.577),1.5+t*.02);
        p.xy=asin(cos(p.xy/2.))*2.;
        p.z=asin(cos(p.z/1.5))*1.5;
        vec2 q=vec2(length(p.xy)-.5,p.z);
        s=6.;
        for(int i=0;i++<7;)
        {
            q=vec2(3.2+sin(t*.5)*.3,2.2)-abs(q-vec2(2.5,1.9+sin(t*1.5)*.07));
            s*=e=7./min(dot(q,q),8.);
            q=q*e;
        }
        g+=e=abs(q.y/s)+.004;
        //c+=mix(vec3(1),H(log(s)*.3),.3)*.015*exp(-.01*i*i*e);
        c+=mix(vec3(1),H(log(s)*.3),.3)*7e-5*exp(-.5*sin(i))/e;

    }
    c*=c*c*c*c;
    O=vec4(c,1);
}
