void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 z = (-iResolution.yx+2.0*fragCoord.yx)/iResolution.y;
    z *= 2.0;
    float s = sin(iTime *0.25);
    z /= 64000.0 * (0.001 + s*s*s*s);
    z.y += 0.054951;
    z.x -= 0.00001;
    vec2 c = vec2(0.25, 0.51 );
    c = 0.44 *cos(c + 3.87) - 0.38*cos(c + 0.1);
    float f = 0.1;
    for (int i = 0; i < 256; i++)
    {
     	z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + c;
		f = min( f, dot(z,z) );
    }

    f = 1.0 - f;
    float background = max(0.0 , 1.0 - f);
    f = max(0.0, f);
    f *= f*f;
    f *= f*f;

    vec3 ca = vec3(1.0,0.5,0.0);
    vec3 cb = vec3(0.0,0.0,1.0);

    vec3 color = mix(ca,cb,f);
    color.x = sin(color.x *32.0 + sin(iTime));
    fragColor = vec4(color,1.0);
}
