void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float ac = 1.5;

    // Time varying pixel color
    float d = distance(fragCoord, iResolution.xy/2.0)/distance(vec2(0.0, 0.0),iResolution.xy/2.0);
    vec3 col = abs(
        d * 100.0 *
        cos(ac*vec3(1.0*iTime,1.0*iTime,0.6*iTime)+uv.xyx+vec3(2,2,2))) * 
        (1.0/(0.5+distance(uv, vec2(0.5, 0.5)))
    );

    col *= exp(d*2.0);

    // Output to screen
    fragColor = vec4(col,1);
}
