#define pi 3.14159265359
#define S smoothstep
vec3 BLACK = vec3(0.);
vec3 WHITE = vec3(1);
vec3 RED = vec3(227,10,23)/255.;
vec3 BLUE = vec3(173,216,237)/255.;
float Star(vec2 uv, float size) {
    uv.x  -= 2.30; // Yıldızın yatay konumu
    float tx = uv.x;
    uv.x = uv.y;
    uv.y = tx;
    uv.x = abs(uv.x);
    float a = 2.0*pi/5.;
    float d1 = dot(uv, vec2(sin(a),cos(a)));

    a = 3.*2.*pi/5.;
    float d2 = dot(uv, vec2(sin(a),cos(a)));


    a = 2.*2.*pi/5.;
    float d4 = dot(uv, vec2(sin(a),cos(a)));

    float d = min(max(d1, d2), max(uv.y, d4));
    float w = min(fwidth(d), 0.05);
    return smoothstep(w, -w, d-size); // d*6.
}
float Circle(vec2 uv, vec2 p, float r, float blur) {
 	float d = length(uv-p);
    float c= smoothstep(r, r-blur, d);
    return c;
}
float Hilal(vec2 uv, float size) {
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    uv.x += .20; // Hilalin yatay konumu
    vec3 col = RED;
    float mask = Circle(uv / size, vec2(0.), .4, .007);
    float mouth = Circle(uv / size, vec2(0., 0.), .3, .007);
    mouth -= Circle(uv / size, vec2(0.08, 0.), .24, .007);
    return mouth;
}
vec2 Remap(vec2 p, float b, float l, float t, float r) {
    return vec2((p.x-l) / (r - l), (p.y - b) / (t - b));
}
vec3 Flag (vec2 uv) {
    float y = sin(uv.y * pi * 13.);
    float w = fwidth(y);
	float stripes = smoothstep(-w, w, y);
    w = fwidth(uv.y);
    vec3 col = RED;

    vec2 st = Remap(uv, .445, .75, 1., .4);
    float size = .375;
    vec2 gv = st*vec2(6,5) - .5;
    if (st.x <= -.7 || st.x >= 2.13) {
        col = BLACK;
        return col;
    }

    col = mix(
        mix(col, RED, smoothstep(0., w*3., st.y)),
        WHITE,
        Hilal(uv, .90)
        );

    col = clamp(max(col, Star(gv, size)), RED, WHITE);
    if (st.x >= 2.08) { // Uçkurluk
        col = mix(col, WHITE, .9);

    }
    col *= smoothstep(w, 0., abs(uv.y-.51)-.51+w);

    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    float t = uv.x*7.-2.*iTime+uv.y*3.; // Dalga efekti
    uv.y += .011 + sin(t)*.03; // Dalga efekti
    uv.x += sin(t)*.0009; // Dalga efekti


    vec3 col = Flag(uv);
    col *= .7+cos(t)*.3; // Dalga efekti
    fragColor = vec4(col,1.0);
}
